const { Nullable } = require('../flow');

function pluck (obj, path) {
    return (path || '').split('.')
    .reduce(function (p, c) {
        if (!p || !p[c]) return null;
        return p[c];
    }, obj);
}

pluck.flow = function () {
    return Nullable(pluck.apply(null, arguments));
}

Object.pluck = pluck;

module.exports = pluck;