const pluck = require('./pluck');

[
    [[{a:{b:{c:'plucked'}}}, 'a.b.c'], 'plucked'],
    [[{a:{b:{c:'plucked'}}}, 'a.b.d'], null]
].map(function (t) {
    test('plucks '+ t[0][1] +' to equal ' + t[1], () => {
        expect(pluck.apply(null, t[0])).toBe(t[1]);
    })
});

[
    [[{a:{b:{c:'plucked'}}}, 'a.b.c'], 'plucked'],
    [[{a:{b:{c:'plucked'}}}, 'a.b.d'], null]
].map(function (t) {
    test('plucks flow '+ t[0][1] +' to equal ' + t[1], () => {
        var a = pluck.flow.apply(null, t[0])
        .map(x => x + 'VALID')
        .fold(
            x => expect(x).toBe(null),
            x => expect(x).toBe(t[1] + 'VALID')
        )
    })
});