const replaceAll = require('./replaceAll');

const tests = [
    ['a-b-c-d', ['-', ''], 'abcd'],
    ['&', ['&', '_'], '_']
]

tests.map(function (t) {
    test(t[0] + ' replace '+ t[1][0] +' with ' + t[1][1], () => {
        expect(replaceAll.apply(t[0], t[1])).toBe(t[2]);
    })
});

tests.map(function (t) {
    test(t[0] + ' proto replace '+ t[1][0] +' with ' + t[1][1], () => {
        expect(t[0].replaceAll.apply(t[0], t[1])).toBe(t[2]);
    })
});