const Flow = x => ({
    map: f => Flow(f(x)),
    fold: f => f(x),
    chain: f => f(x)
})

const Right = x => ({
  map: f => Right(f(x)),
  fold: (f, g) => g(x),
  chain: f => f(x)
})

const Left = x => ({
  map: f => Left(x),
  fold: (f, g) => f(x),
  chain: f => f(x)
})

const Nullable = x => 
    x != null ? Right(x) : Left(x)

const Safely = f => {
  try {
    return Right(f())
  } catch (e) {
    return Left(e)
  }
}

module.exports = {
    Flow, Left, Right, Nullable, Safely 
}